mapper.select:
select userId, shopId, phone, left(accountDay,7) as accountDay, SUM(exchangeInNum) as exchangeInNum, SUM(consumeNum) as consumeNum from consumption_summary
AND shopId = #{shopId}
AND phone = #{phone}
AND UNIX_TIMESTAMP(accountDay) >= unix_timestamp(#{startTime}) AND UNIX_TIMESTAMP(accountDay) < unix_timestamp(#{endTime})
group by userId, shopId, phone, left(accountDay,7) order by userId desc, shopId desc, phone desc, left(accountDay,7) desc

select * from consumption_summary


        AND shopId = #{shopId}


        AND phone = #{phone}

    AND  UNIX_TIMESTAMP(accountDay) >= unix_timestamp(#{startTime})
    AND UNIX_TIMESTAMP(accountDay) < unix_timestamp(#{endTime})

select userId, shopId, phone, left(accountDay,7) as accountDay, SUM(exchangeInNum) as exchangeInNum, SUM(consumeNum) as consumeNum from consumption_summary
AND shopId = #{shopId}
AND phone = #{phone}
AND UNIX_TIMESTAMP(accountDay) >= unix_timestamp(#{startTime}) AND UNIX_TIMESTAMP(accountDay) < unix_timestamp(#{endTime})
group by userId, shopId, phone, left(accountDay,7) order by userId desc, shopId desc, phone desc, left(accountDay,7) desc

select count(*) as totalExchangeCount,
sum(score) as exchangeNum
from score_change

    state = 0
    AND businessCode = 'exchangeIn'

        AND userName = #{phone}

    AND  createTime >= #{startTime}
    AND createTime < #{endTime}

select count(*) as totalConsumptionCount,
sum(score) as consumptionNum
from score_change

    state = 0
    AND businessCode = 'income'

        AND shopId = #{shopId}


        AND theOtherUserName = #{phone}

    AND  createTime >= #{startTime}
    AND createTime < #{endTime}
