mapper.select:
SELECT 
FROM orders

    1=1

        and id = #{id}


        and orderNum = #{orderNum}


        and goodsName = #{goodsName}


        and orderState = #{orderState}


        and orderType = #{orderType}


        and userId = #{userId}


        and shopId = #{shopId}


        and createTime >= #{operateTimeMin}


        and createTime < #{operateTimeMax}

    and source = 1

order by createTime desc

SELECT 
FROM orders


        and shopId = #{shopId}


        and userId = #{userId}


        and orderState in

            #{orderStates[${index}]}



        and orderNum = #{orderNum}


        and goodsName like concat(#{goodsName},'%')


        AND createTime >= #{startTime}


        AND createTime < #{endTime}

    and source = 1

order by shopId asc, createTime desc

select id as orderId, createTime as orderDate, orderNum, barcode, goodsName, goodsCount, price, payment from orders


        and orderNum = #{orderNum}


        and shopId = #{shopId}


        and userId = #{userId}


        and orderState = #{orderState}


        and orderType = #{orderType}

    AND createTime >= #{startTime}
    AND createTime < #{endTime}
    and source = 1

ORDER BY
shopId ASC,createTime desc
