        SELECT
        shopId as shopId,
        count(id) as quantity,
        sum(payment) as totalAmount,
        sum(freight) as totalFreight,
        #{startTime} as startTime,
        #{endTime} as endTime
        FROM
        orders


                and shopId = #{shopId}


                and orderState in

                    #{orderStates[${index}]}


            AND createTime >= #{startTime}
            AND createTime < #{endTime}
            and source = 1

        GROUP BY
        shopId
        ORDER BY
        shopId ASC




