                and createTime >= #{operateTimeMin}


                and createTime < #{operateTimeMax}

            and source = 1

        order by createTime desc




        SELECT 
        FROM orders


                and shopId = #{shopId}


                and userId = #{userId}


                and orderState in

                    #{orderStates[${index}]}



                and orderNum = #{orderNum}


                and goodsName like concat(#{goodsName},'%')


                AND createTime >= #{startTime}


                AND createTime < #{endTime}

            and source = 1

        order by shopId asc, createTime desc



        select id as orderId, createTime as orderDate, orderNum, barcode, goodsNo, goodsName, goodsCount, orderState, price, payment from orders


                and orderNum = #{orderNum}


                and shopId = #{shopId}


                and userId = #{userId}


                and orderState = #{orderState}


                and orderType = #{orderType}


    			AND DATE_FORMAT(createTime, '%Y-%m-%d')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d')   


    			AND DATE_FORMAT(createTime, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')    

            and source = 1

        ORDER BY
        shopId ASC,createTime desc



        select id as orderId, createTime as orderDate, orderNum, shopId, username, barcode, goodsName, payment,goodsCount from orders


                and shopId = #{shopId}


                and userId = #{userId}


    			AND DATE_FORMAT(createTime, '%Y-%m-%d')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d')   


    			AND DATE_FORMAT(createTime, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')    

            and source = 1

        ORDER BY
        shopId ASC,createTime ASC



        select ord.createTime, ordx.returnTime,ord.orderNum, ord.shopId, ord.username, ord.barcode, ord.goodsName,ord.payment,ord.goodsCount
        from orders ord ,orders_extend ordx,shop sh

            ord.id = ordx.orderId and ord.

                and ord.shopId = #{shopId}


                and ord.userId = #{userId}


                AND DATE_FORMAT(ordx.retureTime, '%Y-%m-%d')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d')   


                AND DATE_FORMAT(ordx.retureTime, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')    

            and ord.source = 1
            and ord.orderState = 5

        ORDER BY ordx.returnTime desc





