mapper.select:
select * from score_change


        and userId = #{userId}


        and businessCode in

            #{businessCodeList[${index}]}



        and (
        shopId = #{shopId}

            or (exchangeCurrency = #{exchangeCurrency})

        )


        and operationTime >= #{operateTimeMin}


        and operationTime < #{operateTimeMax}

    and state = 0

order by createTime desc

select * from score_change


        and userId = #{userId}


        and businessCode in

            #{businessCodeList[${index}]}




            and (
            shopId = #{shopId}

                or (exchangeCurrency = #{exchangeCurrency})

            )


            and (shopId is not null or exchangeCurrency is not null)



        and operationTime >= #{operateTimeMin}


        and operationTime < #{operateTimeMax}

    and state = 0

order by createTime desc

select operationTime as serialDate,
orderCode as serialNum,
exchangeCurrency,
foreignCurrency,
score,
userName as phone
from score_change


        and userId = #{userId}


        and businessCode in

            #{businessCodeList[${index}]}




            and (
            shopId = #{shopId}

                or (exchangeCurrency = #{exchangeCurrency})

            )


            and (shopId is not null or exchangeCurrency is not null)



        and operationTime >= #{operateTimeMin}


        and operationTime < #{operateTimeMax}

    and state = 0

order by createTime desc
